yLongitude`, `featureCrewManagement`, `featureQuickbooks`, `quickbooksStatus`, `quickbooksDefaultService`, `companyActive`) VALUES (NULL, '1', '1', NULL, 'Earth Contact Products', '15612 S Keeler Terrace', '', 'Olathe', 'KS', '66062', NULL, NULL, NULL, NULL, NULL, 'www.earthcontactproducts.com', NULL, '#3694d1', '#646464', '&lt;p&gt;We&#039;d like to thank you for giving ECP the opportunity to assess and consult your home repair needs. I will be in touch with you to schedule an evaluation of your home and&nbsp;provide you an estimate for 123 Main St.&nbsp;&lt;br /&gt;&lt;br /&gt; Feel free to contact me anytime&nbsp;about any of the services we provide:&lt;/p&gt;
